INSERT INTO eventitask.task (section_id, name, description, priority, fibonacci, status, time, data_estimada, date_creation) VALUES 
    (10, 'Correção de Avaliação na Home / Formulário Dinâmico e Tela de Buffet', ' Refatorar código que puxa valores das avaliações dentro da tela inicial. ', 2, '5', 2, 2, '2023-09-18 00:00:00', '2023-09-16 00:00:00'),
    (13, 'Revisão do Front-end', ' Revisar se todas as telas do front-end estão devidamente conectadas ao back-end. Assim validando se o projeto está pronto para ser apresentado na sprint de 21 de setembro. ', 2, '8', 2, 4, '2023-09-16 00:00:00', '2023-09-12 00:00:00'),
    (15, 'Serviços para FE na Cloud', ' Criar código shell script que faça a instalação e execução dos serviços de front-end no docker na EC2. Criar um repositório nomeado (server-config) no git para guardar esses arquivos e uma EC2 na AWS para ficar testando eles. Não esquecer de instalar o No', 2, '13', 2, 6, '2023-08-23 00:00:00', '2023-08-17 00:00:00'),
    (13, 'Log de acesso', ' Assim que o usuário utilizar alguma das seguintes páginas:   - cadastro de usuário - cadastro de buffet - formulário dinâmico - contratante   ', 2, '21', 2, 10, '2023-07-31 00:00:00', '2023-07-21 00:00:00'),
    (13, 'Paginação (Pesquisa - Formulário)', ' Criar requisições elementos na tela para que de maneira fluída, assim que o usuário tentar ver mais elementos no sistema, a requisição seja executada e seja devolvido mais elementos vindos do back-end. Sugestão: Fazer por meio de setas ou de acordo com a ', 2, '21', 2, 10, '2023-06-26 00:00:00', '2023-06-16 00:00:00'),
    (10, 'Sugestão de Buffets em resultados vazios', 'Implementar tanto nos resultados do formulário dinâmico quanto na pesquisa o seguinte mecanismo: Quando o resultado for vazio, o sistema deverá retornar uma linha de buffets que sejam sugestões para o usuário. Isso melhorará a usabilidade do site e não dei', 2, '8', 2, 4, '2023-06-13 00:00:00', '2023-06-09 00:00:00'),
    (10, 'Refatoração dos Resultados', 'Refatorar a estilização da tela de resultados do formulário dinâmico. Deixando um buffet em destaque e outros caso o usuário desejar pesquisar mais.  Vide prototipagem E9.', 2, '5', 2, 2, '2023-06-11 00:00:00', '2023-06-09 00:00:00'),
    (10, 'Componentização e estilização do Formulário Dinâmico', ' Componentizar todas as etapas do formulário dinâmico para facilitar o desenvolvimento e a url do usuário não seja alterada a cada etapa, fazendo assim com que o fluxo esteja mais suave e o sistema com melhor desempenho.  Desejável: Inserir um componente q', 2, '21', 2, 10, '2023-06-19 00:00:00', '2023-06-09 00:00:00'),
    (10, 'Refatoração da Tela de Perfil', 'Existem dados na tela de perfil que não existem no banco de dados. Implementar input para que o usuário insira uma nova foto. Por enquanto esse input não precisa estar funcional, aguardando o uso de sistema de armazenamento de arquivos para o próximo semes', 2, '5', 2, 2, '2023-06-11 00:00:00', '2023-06-09 00:00:00'),
    (13, 'Funcionalidade de Endereço', ' Criar funcionalidade na qual o administrador consiga visualizar o endereço que foi cadastrado pelo usuário (proprietário) e validar ou invalidar o endereço solicitado. Também deverá ter uma maneira na qual o usuário consiga verificar que seu endereço não ', 2, '21', 2, 10, '2023-06-19 00:00:00', '2023-06-09 00:00:00'),
    (10, 'Adaptação das Regras de Segurança', ' Alocar em todas as páginas que necessitam de autenticação, o Bearer Token na camada de autenticação da requisição para que o usuário não tenha acesso a conteúdo indevido na plataforma durante as requisições. ', 2, '5', 2, 2, '2023-06-11 00:00:00', '2023-06-09 00:00:00'),
    (10, 'Complementos na tela de chat', 'Quando o usuário não ter nenhum chat dentro do banco, deverá ser tratado com uma mensagem Não há nenhuma conversa.  Inserir botão de envio do chat.', 2, '5', 2, 2, '2023-06-11 00:00:00', '2023-06-09 00:00:00'),
    (10, 'Adaptação de requisições de acordo com o banco', ' Revisar todas as entradas de dados do front-end (inputs) e adaptar eles para que fique de acordo com as regras inseridas no banco de dados.  Exemplo: O campo e-mail deve ter até 255 caracteres (VARCHAR 255). Adaptar essa regra para o input de e-mail no ca', 2, '8', 2, 4, '2023-06-13 00:00:00', '2023-06-09 00:00:00'),
    (10, 'Bloquear acesso de usuários inválidos', 'Quando um usuário banido tentar entrar na plataforma, redirecionar ele para uma tela pedindo para que ele entre em contato com suporte@eventify.com para tentar o desbloqueio da plataforma.  Também tratar em todas as telas caso o tipo de usuário (sessionSto', 2, '5', 2, 2, '2023-06-11 00:00:00', '2023-06-09 00:00:00'),
    (10, 'Máscara de data e dinheiro', ' Revisar o sistema em busca de inputs que necessitam de algum tipo de tratamento de dados na hora do usuário inserir.  Recomendável que se crie um componente apenas de máscara. Mas isso deve ser validado se é uma boa prática de programação. ', 2, '5', 2, 2, '2023-06-11 00:00:00', '2023-06-09 00:00:00'),
    (10, 'Refatoração da tela de cadastro', 'Deve manter explícito a diferença de um usuário contratante para um usuário proprietário. Inserir demais máscaras e tratar espaçamentos entre os inputs que precisam de respiro.  Também insira um input para o usuário inserir sua foto no sistema. Este input ', 2, '5', 2, 2, '2023-06-11 00:00:00', '2023-06-09 00:00:00'),
    (10, 'Tratamento de loading', ' Desenvolver ou reutilizar um componente de carregamento para melhorar a usabilidade do usuário. ', 2, '5', 2, 2, '2023-06-11 00:00:00', '2023-06-09 00:00:00'),
    (14, 'Integração com a API Efí', ' Substituir a service do endpoint de pagamentos para a API Efí para simular o pagamento real do sistema. Documentação: https://dev.gerencianet.com.br/docs/api-pix-introducao  Detalhe essêncial: Enviar requisições que peçam o pagamento de apenas 1 centavo. ', 2, '21', 2, 10, '2023-06-19 00:00:00', '2023-06-09 00:00:00'),
    (10, 'Refatoração dos diretórios', 'Refatorar de acordo com as melhores práticas a estrutura dos diretórios do projeto.', 2, '5', 2, 2, '2023-06-11 00:00:00', '2023-06-09 00:00:00'),
    (10, 'Correção de bugs de Pesquisa', 'Corrigir toda a tela de pesquisa que depende do usuário assinalar os checkbox, o input do componente Header, independente de onde ele estiver e pesquisar em conjunto o nome e os filtros.', 2, '5', 2, 2, '2023-06-11 00:00:00', '2023-06-09 00:00:00'),
    (10, 'Refatoração do Rodapé', ' Refatorar o rodapé da página para que ela tenha dados fictícios da empresa Eventify. Colocar logo e referências do Eventify. ', 2, '3', 2, 1, '2023-06-10 00:00:00', '2023-06-09 00:00:00'),
    (14, 'Log de acesso', ' Assim que o usuário utilizar alguma das seguintes páginas:   - cadastro de usuário - cadastro de buffet - formulário dinâmico - contratante   ', 2, '21', 2, 10, '2023-07-31 00:00:00', '2023-07-21 00:00:00'),
    (14, 'Delete por Update', ' Trocar na modelagem do banco de dados, nos atributos e em todos os demais lugares a opção de remoção de um registro, apenas acendendo uma flag de que o registro não deve estar mais disponível para leitura do usuário. ', 2, '21', 2, 10, '2023-08-04 00:00:00', '2023-07-25 00:00:00'),
    (15, 'Avaliação de Grupo Externo', ' Criar documento que avalia a apresentação do grupo de um grupo externo de PI. ', 2, '5', 2, 2, '2023-09-14 00:00:00', '2023-09-12 00:00:00'),
    (14, 'Recursividade', ' Transformar algum método (validar antes com o grupo) em recursivo, seguindo assim o que foi pedido pela professora Célia na tarefa de Paradigmas. ', 2, '8', 2, 4, '2023-08-07 00:00:00', '2023-08-03 00:00:00'),
    (13, 'Estilização de Cadastro de Buffet', ' Corrigir a estilização da tela de cadastro para uma versão mais confortável para o usuário.  Trocar espaçamento entre elementos, fixar tamanho da fonte e trocar estilização do input de descrição. ', 2, '8', 2, 4, '2023-06-13 00:00:00', '2023-06-09 00:00:00'),
    (14, 'Tabela Hashing (espalhamento) com lista encadeada', ' Implementar no back-end a estrutura de tabela hashing no algoritmo de busca. Ela será utilizado para aferição do entregavel da disciplina de Paradigmas da Professora Célia. ', 2, '8', 2, 4, '2023-08-07 00:00:00', '2023-08-03 00:00:00'),
    (13, 'Caminho para Criação de Buffet', ' Criar caminho para tela de cadastro de buffet na tela de perfil do usuário contratante. ', 2, '5', 2, 2, '2023-08-26 00:00:00', '2023-08-24 00:00:00'),
    (15, 'Serviço (docker) para Banco de Dados na EC2', ' Criar código shell script que faça a instalação e execução dos serviços docker na EC2. Criar um repositório nomeado (server-config) no git para guardar esses arquivos e uma EC2 na AWS para ficar testando eles. Não esquecer de trocar o JDK da 11 para a 17.', 2, '13', 2, 6, '2023-08-09 00:00:00', '2023-08-03 00:00:00'),
    (15, 'Serviços (dockers) para SpringBoot na EC2', ' Criar código shell script que faça a instalação e execução dos serviços docker na EC2. Criar um repositório nomeado (server-config) no git para guardar esses arquivos e uma EC2 na AWS para ficar testando eles. Não esquecer de trocar o JDK da 11 para a 17.', 2, '13', 2, 6, '2023-08-09 00:00:00', '2023-08-03 00:00:00'),
    (15, 'Desenho de Arq. Redes com Serviços', ' Criar o desenho detalhado da arquitetura de redes incluindo os serviços da AWS. Isso envolverá a elaboração de um plano visual que represente a disposição dos componentes da rede, a conectividade entre eles, bem como a alocação de recursos e equipamentos.', 2, '8', 2, 4, '2023-08-07 00:00:00', '2023-08-03 00:00:00'),
    (15, 'Desenho de Arq. Redes', ' Criar o desenho detalhado da arquitetura de redes do projeto. Isso envolverá a elaboração de um plano visual que represente a disposição dos componentes da rede, a conectividade entre eles, bem como a alocação de recursos e equipamentos. O objetivo é forn', 2, '8', 2, 4, '2023-08-07 00:00:00', '2023-08-03 00:00:00'),
    (15, 'Plano de endereçamento IP', ' Criar documentação (colocar em anexo da tarefa) que deixe explicito o plano de endereçamento de IP da nuvem do Eventify. Tratar em sala de aula e validar com o Alex e o Márcio se o documento está correto e satisfaz a necessidade da tarefa da sprint. ', 2, '8', 2, 4, '2023-08-07 00:00:00', '2023-08-03 00:00:00'),
    (15, 'Apresentação Sprint IV', ' Criar no Power Point sequência da apresentação da Sprint IV e desenvolver todo o fluxo do roteiro do que cada um dos integrantes irão falar. ', 2, '5', 2, 2, '2023-08-29 00:00:00', '2023-08-27 00:00:00'),
    (15, 'Configuração Project Libre', ' Configurar ProjectLibre do Eventify para a apresentação. ', 2, '8', 2, 4, '2023-09-09 00:00:00', '2023-09-05 00:00:00'),
